import { Button, HelperText, Input, Label } from "@windmill/react-ui";
import { useUser } from "context/UserContext";
import React, { useEffect } from "react";
import { useForm } from "react-hook-form";
import { Link } from "react-router-dom";

const PaymentForm = ({ next }) => {
  const { userData } = useUser();
  const { register, handleSubmit, errors, setValue } = useForm();

  useEffect(() => {
    setValue("fullname", userData?.fullname);
    setValue("email", userData?.email);
    setValue("address", userData?.address);
    setValue("country", userData?.country);
    setValue("city", userData?.city);
    setValue("state", userData?.state);
  }, [setValue, userData]);

  return (
    <div className="w-full">
      <h1 className="text-3xl text-center mb-4 font-semibold">
        Address Details
      </h1>
      <form
        className="border p-4 border-black-4 w-full md:w-1/2 mx-auto"
        onSubmit={handleSubmit((data) => next(data))}
      >
        <Label className="block text-grey-darker text-sm font-bold mb-4">
          <span>Fullname</span>
          <Input
            disabled
            type="text"
            className="shadow appearance-none border rounded w-full text-grey-darker mt-2 px-2 py-2 border focus:outline-none"
            name="fullname"
            ref={register({ required: "Required" })}
          />
          {errors.fullname && (
            <HelperText valid={false}>{errors.fullname.message}</HelperText>
          )}
        </Label>
        <Label className="block text-grey-darker text-sm font-bold mb-4">
          <span>Email</span>
          <Input
            disabled
            className="shadow appearance-none border rounded w-full text-grey-darker mt-2 px-2 py-2 border focus:outline-none"
            type="text"
            name="email"
            ref={register({ required: "Required" })}
          />
          {errors.email && (
            <HelperText valid={false}>{errors.email.message}</HelperText>
          )}
        </Label>
        <Label className="block text-grey-darker text-sm font-bold mb-4">
          <span>Address</span>
          <Input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-grey-darker mt-2 px-2 py-2 border focus:outline-none"
            type="text"
            name="address"
            ref={register({ required: "Required" })}
          />
          {errors.address && (
            <HelperText valid={false}>{errors.address.message}</HelperText>
          )}
        </Label>
        <Label className="block text-grey-darker text-sm font-bold mb-4">
          <span>Country</span>
          <Input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-grey-darker mt-2 px-2 py-2 border focus:outline-none"
            type="text"
            name="country"
            ref={register({ required: "Required" })}
          />
          {errors.country && (
            <HelperText valid={false}>{errors.country.message}</HelperText>
          )}
        </Label>
        <Label className="block text-grey-darker text-sm font-bold mb-4">
          <span>State/Region</span>
          <Input
            className="shadow appearance-none border rounded w-full py-2 px-3 text-grey-darker mt-2 px-2 py-2 border focus:outline-none"
            type="text"
            name="state"
            ref={register({ required: "Required" })}
          />
          {errors.state && (
            <HelperText valid={false}>{errors.state.message}</HelperText>
          )}
        </Label>
        <Label className="block text-grey-darker text-sm font-bold mb-4">
          <span>City</span>
          <Input
            className="shadow appearance-none border rounded w-full text-grey-darker mt-2 px-2 py-2 border focus:outline-none"
            type="text"
            name="city"
            ref={register({ required: "Required" })}
          />
          {errors.city && (
            <HelperText valid={false}>{errors.city.message}</HelperText>
          )}
        </Label>
        <div className="flex justify-between">
          <Button tag={Link} to="/cart" layout="outline" size="small">
            Back to cart
          </Button>
          <Button type="submit" size="small">
            Proceed
          </Button>
        </div>
      </form>
    </div>
  );
};

export default PaymentForm;
